import { useState, useEffect } from 'react'
import cartimg from '../images/cart.svg'
import './cart.css'
import uniqid from 'uniqid'

const Cart = (props) => {
    const cart = props.sendCart

    return (
        <div className="cart"><img src={cartimg} alt='cart symbol'></img><span>{cart.length}</span></div>
    )
}

const OneProduct = (props) => {
    return (<div className='productincart'><div className='name'>{props.name}</div> <div className='price'>Price: {props.price} $</div><div className='quantity'>Quantity: {props.quantity}</div></div>)
}

const InsideCart = (props) => {
    const cartTemporary = props.sendCart
    const [cart, setCart] = useState([])
    const [total, setTotal] = useState({price: 0, quantity: 0})

    const updateTotal = () => {
        const newTotal = cart.map(product => {
            const price = 0
            price + product.price * product.quantity
            const quantity = quantity + product.quantity
        })
    }

    useEffect(() => {
        setCart(cartTemporary)
    }, [cartTemporary])

    return (

        <div className='insidecart'>
            {cartTemporary.map(product => <OneProduct name={product.name} price={product.price} quantity={product.quantity} key={uniqid()} />)}
            <div className='totalprice'><div className='price'>Total price: <b>{total.price} $</b></div><div className='quantitysum'>Total quantity: <b>{total.quantity}</b></div></div>
        </div>
    )
}

export { Cart, InsideCart }
