import { useState, useEffect } from 'react'
import cartimg from '../images/cart.svg'
import './cart.css'
import uniqid from 'uniqid'

const Cart = (props) => {
    const cart = props.sendCart

    return (
        <div className="cart"><img src={cartimg} alt='cart symbol'></img><span>{cart.length}</span></div>
    )
}

const OneProduct = (props) => {
    return (<div className='productincart'><div className='name'>{props.name}</div> <div className='price'>Price: {props.price}</div><div className='quantity'>{props.quantity}</div></div>)
}

const InsideCart = (props) => {
    const cartTemporary = props.sendCart
    const [cart, setCart] = useState([])

    useEffect(() => {
        setCart(cartTemporary)
    }, [cartTemporary, cart])

    const updateCartState = () => {}

    const showCart = () => {
        console.log(cart)
    }


    return (

        <div className='insidecart'>
            {cart.map(product => <OneProduct name={product.name} price={product.price} quantity={product.quantity} key={uniqid()} />)}
            <button onClick={showCart}>SHOW CART</button>
        </div>
    )
}

export { Cart, InsideCart }
